import java.io.BufferedReader;import java.io.*;import java.io.InputStreamReader;import java.io.OutputStream;import java.net.HttpURLConnection;import java.net.URL;import java.text.MessageFormat;import java.util.Scanner;public class TestAPIjava{	public static void main(String args[]){		call_me();		System.out.println("trial");	}	public static void call_me() {		try{						URL url = new URL("https://midtermprojectapi.herokuapp.com/predict");			HttpURLConnection httpURLConnection=(HttpURLConnection)url.openConnection();			httpURLConnection.setRequestMethod("POST");			httpURLConnection.setRequestProperty("Content-Type", "application/json; utf-8");            		httpURLConnection.setRequestProperty("Accept", "application/json");			httpURLConnection.setDoOutput(true);			Scanner scanner = new Scanner(System.in);			String city = scanner.nextLine();			String post_data = String.format("[{\"city\": \"%s\",\"area\": \"Muthaiga\", \"bedrooms\":10, \"bathrooms\": 10, \"size\": 10, \"parking\": 5}]",city);            		byte[] input = post_data.getBytes("utf-8");            		OutputStream os = httpURLConnection.getOutputStream();            		os.write(input, 0, input.length);            		os.close();           		System.out.println(post_data);           		System.out.println("result after Reading JSON Response");           		InputStreamReader inputStreamReader=new InputStreamReader(httpURLConnection.getInputStream());            		BufferedReader bufferedReader=new BufferedReader(inputStreamReader);            		StringBuilder response=new StringBuilder();            		String line="";            		while ((line=bufferedReader.readLine())!=null){                		response.append(line);            		}            		bufferedReader.close();            		System.out.println("Response : "+response.toString());           		httpURLConnection.disconnect();		}		catch(Exception e){			e.printStackTrace();			System.out.println("Error in Making POST Request");		}			}}